import React, { Component } from "react";
import "./App.css";
import { Button } from "react-bootstrap";

import Header from "./MyHeader";
import SearchBar from "./MySearchBar";
var APIid = "2x21yf8b7p6a6z6agpbc4cdsf0cy8d";
var url = "https://api.twitch.tv/helix/streams?game_id=33214";
var games = [
  "Fortnite",
  "Grand Theft Auto V",
  "League of Legends",
  "Just Chatting",
  "Apex Legends",
  "Dota 2",
  "Counter - Strike: Global Offensiver",
  "Tom Clancy's The Division 2",
  "Overwatch",
  "Hearthstone",
  "World of Warcraft",
  "Path of Exile",
  "Player Unknown Battlegrounds",
  "Tom Clancy's Rainbow Six: Siege",
  "Dead by Daylight",
  "Sea of Thieves",
  "Old School RuneScape",
  "Special Events",
  "Call of Duty: Black Ops 4"
];
var gameIds = [
  33214,
  32982,
  21779,
  509658,
  511224,
  29595,
  32399,
  504463,
  488552,
  138585,
  18122,
  29307,
  493057,
  460630,
  491487,
  490377,
  459931,
  509663,
  504462
];

// https://api.twitch.tv/helix/streams?
// https://api.twitch.tv/kraken/streams/
// https://api.twitch.tv/helix/streams?game_id=33214

class App extends Component {
  constructor(props) {
    super(props);
    this.search = this.search.bind(this);
    this.state = {
      items: [],
      loading: false
    };
  }

  componentDidMount() {
    this.setState({ loading: true });
    const test_access_token = () => {
      fetch(url, {
        headers: {
          "Client-Id": APIid
        }
      })
        .then(res => res.json())
        .then(json => {
          this.setState({
            items: json.data,
            loading: false
          });
          console.log("json response: ", json);
          console.log("this.state response: ", this.state);
          console.log("Index zero game: ", json.data[0].game);
          console.log("Index zero game: ", this.state.items[0].id);
          console.log(this.state.items[2]);
          console.log(gameIds.indexOf(33214));
          // { games[gameIds.findIndex(item.game_id)] }
        });
    };
    test_access_token();
  }

  search(event) {}

  render() {
    var { loading, items } = this.state;
    if (loading) {
      return <div>Loading...</div>;
    } else {
      return (
        <div className="box">
          <Header />
          <SearchBar />
          <div>
            {this.state.items.map(item => (
              <div className="stream container">
                <div className="row">
                  <div className="stream-image col-sm-4">
                    <img src={item.language} alt="" />
                  </div>
                  <div className="stream-info col-sm-7">
                    <h5 className="name">{item.user_name}</h5>
                    <p className="game">{item.game_id}</p>
                    <p className="viewers">Viewers: {item.viewer_count}</p>
                    <p className="description">{item.title}</p>
                  </div>
                </div>
                <hr />
              </div>
            ))}
          </div>
        </div>
      );
      // return (
      //   <div className="box">
      //     <Header />
      //     <SearchBar />
      //     {myStreams}
      //     <Stream
      //       stream={{
      //         image: "test",
      //         name: "test",
      //         game: "Fort",
      //         viewers: "5",
      //         description: "test"
      //       }}
      //     />
      //     <Button bsstyle="success">Test Button</Button>
      //   </div>
      // );
    }
  }
}

export default App;

// The stream needs to be passed attributes
