import React, { Component } from "react";
import "./App.css";
import { Button } from "react-bootstrap";

import Header from "./MyHeader";
import SearchBar from "./MySearchBar";
import Stream from "./MyStream";
// import Background from "./MyBackground";
var APIid = "2x21yf8b7p6a6z6agpbc4cdsf0cy8d";

class App extends Component {
  constructor() {
    super();
    this.state = {
      loading: false,
      info: []
    };
  }

  componentDidMount() {
    this.setState({ loading: true });
    const test_access_token = () => {
      fetch("https://api.twitch.tv/kraken/streams/", {
        headers: {
          "Client-Id": APIid
        }
      })
        .then(response => response.json())
        .then(data => {
          this.setState({
            loading: false,
            info: data
          });
          console.log("response: ", data);
          console.log("Index zero: ", data.streams[0].game);
        });
    };
    test_access_token();
  }

  render() {
    var { loading, info } = this.state;
    const text = "";
    if (loading) {
      text = "Loading...";
    }

    return (
      <div className="box">
        <p>{text}</p>
        <Header />
        <SearchBar />
        <Stream
          stream={{
            image: "test",
            name: "test",
            game: "test",
            viewers: "test",
            description: "test"
          }}
        />
        <Button bsstyle="success">Test</Button>
      </div>
    );
  }
}

export default App;

