class App extends Component {
  constructor() {
    super();
    this.state = {};
  }

  componentDidMount() {
    // var self = this;
    var listRequest = new XMLHttpRequest();
    console.log("make request");
    listRequest.open(
      "GET",
      "curl -H 'Client-ID: 2x21yf8b7p6a6z6agpbc4cdsf0cy8d' -X GET 'https://api.twitch.tv/helix/streams?game_id=33214'",
      true
    );
    listRequest.setRequestHeader("Client-ID", id);
    listRequest.send();
    console.log(this.responseText);
    console.log(listRequest);
    console.log("done");
  }

  render() {
    return (
      <div className="box">
        <Header />
        <SearchBar />
        <Stream />
        <Button bsstyle="success">Test</Button>
      </div>
    );
  }
}

